import React, { useState } from 'react';
import './sellerform.css'
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';
export const SellerForm = () => {
      const [images, setImages] = useState([]);
       const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };

    return (
        <div>
            <form className="form-container">
                <div className="form-group">
                    <label htmlFor="name" className="form-label">Tên Shop</label>
                    <input id="name" type="text" className="form-input" placeholder="Nhập vào đây" />
                </div>
                <div className="form-group">
                    <label htmlFor="address" className="form-label">Địa chỉ</label>
                    <textarea id="description" className="form-input" rows="4" placeholder="Nhập vào đây" ></textarea>
                </div>
                <div className="form-row">
                    <div className="form-group small">
                        <label htmlFor="phone" className="form-label">Điện thoại</label>
                        <input id="phone" type="number" className="form-input" placeholder="Nhập vào đây" />
                    </div>
                </div>
                
                <div className="form-group">
                    <label className="form-label">Avatar</label>
                    <div className="image-upload-group">
                        {Array(1).fill('').map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="images"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={
                                        images[index]
                                            ? URL.createObjectURL(images[index])
                                            : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"
                                    }
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>
                <button type="submit" className="submit-btn">ADD</button>

            </form>
        </div>
    )
}
