import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { useParams, Link } from 'react-router-dom';
import { Button, Rate, Row, Col } from 'antd';
import { toSlug } from '../../utils/toSlug';
import productData from '../../assets/data/product';
import { useAppContext } from '../../context/AppContext';
export const ProductDetails = () => {

  const { product, navigate } = useAppContext()
  const { id } = useParams()
  const [relatedProducts, setRelatedProducts] = useState(product.img[0])
  const [thumbnail, setThumbnail] = useState(null);
  const currentProduct = product.find((item) => item.id === id);

  useEffect(() => {
    if (product.length > 0) {
      let productsCopy = product.slice();
      productsCopy = productsCopy.filter((item) => currentProduct.category === item.category);
      setRelatedProducts(productsCopy.slice(0, 5));
    }
  }, [product]);

  useEffect(() => {
    setThumbnail(product?.img?.[0] || null);
  }, [product]);

  return (
    <>
      <nav>
        <p className="breadcrumb">
          <Link to={"/"}>Trang chủ</Link> /
          <Link to={`/product/${toSlug(product.category)}`}>Sản phẩm</Link> /
          <span>{product.category}</span> /
          <span className="highlight">{product.name}</span>
        </p>
      </nav>
    </>
  );
};
