import React, { useEffect, useState } from 'react';
import './product.css';
import { Col, Row, Pagination } from 'antd';
import { useNavigate } from 'react-router-dom';
import { toSlug } from '../../utils/toSlug';
import axios from 'axios';

export const Product = () => {
  const [products, setProducts] = useState([]);
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);          // üëà t·ªïng s·∫£n ph·∫©m
  const [totalPages, setTotalPages] = useState(1);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await axios.get(`/api/products/all?page=${page}&limit=12`);
        setProducts(res.data.products);
        setTotal(res.data.total);
        setTotalPages(res.data.totalPages);
      } catch (err) {
        console.error(err);
      }
    };
    fetchProducts();
  }, [page]);

  if (!products || products.length === 0) {
    return <div>Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o</div>;
  }

  return (
    <section className="product">
      <Row className="product__title">
        <h4>G·ª£i √Ω h√¥m nay</h4>
      </Row>

      <Row>
        <Col span={24}>
          <div className="product__list">
            {products.map((item) => (
              <div
                key={item._id}
                className="product__item"
                onClick={() =>
                  navigate(
                    `/product/${toSlug(item?.categoryId?.name || 'danh-muc')}/${item._id}`
                  )
                }
              >
                <img src={item.image?.[0]} alt={item.name} className="product__item-img" />
                <div className="product__item-info">
                  <p className="product__item-name">{item.name}</p>
                  <span className="product__item-price">
                    {item.price.toLocaleString()}‚Ç´
                  </span>
                </div>
              </div>
            ))}
          </div>
        </Col>
      </Row>

      <Pagination
        current={page}
        total={total}         // üëà s·ªë l∆∞·ª£ng s·∫£n ph·∫©m
        pageSize={12}         // üëà s·ªë s·∫£n ph·∫©m m·ªói trang
        onChange={(p) => setPage(p)}
        showSizeChanger={false}
        style={{ marginTop: '1rem', textAlign: 'center' }}
      />
    </section>
  );
};
