import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './customerForm.css';

const CustomerForm = () => {
    const [formData, setFormData] = useState({
        _id: '',
        name: '',
        email: '',
        phone: '',
        birthday: '',
        gender: '',
    });

    // Load dữ liệu từ localStorage khi component mount
    useEffect(() => {
        const storedCustomer = JSON.parse(localStorage.getItem('customer'));
        if (storedCustomer) {
            setFormData(storedCustomer);
        }
    }, []);

    // Xử lý thay đổi input
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    // Tạo khách hàng mới
    const handleCreate = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:3002/api/customer/create', formData);
            alert('Lưu thông tin thành công!');
            
            // Đảm bảo rằng _id được lưu vào localStorage
            const customerWithId = { ...res.data.customer }; // Gộp thông tin trả về với _id
            localStorage.setItem('customer', JSON.stringify(customerWithId));
            setFormData(customerWithId); // Cập nhật formData với _id
        } catch (error) {
            console.error('Lỗi khi tạo khách hàng:', error);
            alert('Tạo khách hàng thất bại!');
        }
    };
    

    const handleUpdate = async (e) => {
        e.preventDefault();
        // Kiểm tra nếu _id chưa có, sẽ không cho cập nhật
        if (!formData._id) {
            alert('Không có ID khách hàng để cập nhật!');
            return;
        }
    
        try {
            const res = await axios.put(`http://localhost:3002/api/customer/update/${formData._id}`, formData);
            alert('Cập nhật thông tin thành công!');
            localStorage.setItem('customer', JSON.stringify(res.data.customer)); // Lưu lại khách hàng mới
            setFormData(res.data.customer); // Cập nhật lại formData với thông tin mới
        } catch (error) {
            console.error('Lỗi khi cập nhật thông tin khách hàng:', error);
            alert('Cập nhật thất bại!');
        }
    };
    

    // Làm mới form (xóa dữ liệu hiện tại)
    const handleReset = () => {
        setFormData({
            _id: '',
            name: '',
            email: '',
            phone: '',
            birthday: '',
            gender: '',
        });
        localStorage.removeItem('customer');
    };

    return (
        <div className="customer-form-overlay">
            <div className="customer-form">
                <h3>Thông tin khách hàng</h3>
                <form>
                    <input
                        name="name"
                        value={formData.name}
                        placeholder="Họ tên"
                        onChange={handleChange}
                        required
                    />
                    <input
                        name="email"
                        value={formData.email}
                        placeholder="Email"
                        onChange={handleChange}
                        required
                    />
                    <input
                        name="phone"
                        value={formData.phone}
                        placeholder="Số điện thoại"
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="date"
                        name="birthday"
                        value={formData.birthday}
                        onChange={handleChange}
                    />
                    <select name="gender" value={formData.gender} onChange={handleChange}>
                        <option value="">Chọn giới tính</option>
                        <option value="Nam">Nam</option>
                        <option value="Nữ">Nữ</option>
                        <option value="Khác">Khác</option>
                    </select>

                    <div className="actions">
                        <button type="button" onClick={handleCreate} style={{ marginLeft: '10px' }}>Lưu</button>
                        <button onClick={handleUpdate}>Cập nhật</button>

                    </div>

                </form>
            </div>
        </div>
    );
};

export default CustomerForm;
