import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import './order.css';

export const Order = () => {
    const { cartItems, products } = useAppContext();
    const [paymentMethods, setPaymentMethods] = useState([]);
    const [paymentMethod, setPaymentMethod] = useState('cod');
    const [formData, setFormData] = useState({
        fullName: '',
        email: '',
        phone: '',
        city: '',
        address: ''
    });

    useEffect(() => {
        const fetchPaymentMethods = async () => {
            try {
                const response = await axios.get('http://localhost:3008/api/paymentMethod/all');
                setPaymentMethods(response.data);
            } catch (error) {
                console.error('Error fetching payment methods:', error);
            }
        };
        fetchPaymentMethods();
    }, []);

    const selectedItems = cartItems.filter(item => item.selected);

    const getProductById = (id) => products.find((p) => p._id === id);

    const totalPrice = selectedItems.reduce((acc, item) => {
        const prod = getProductById(item.productId);
        if (!prod) return acc;
        const price = prod.discounted_price || prod.price || 0;
        return acc + price * item.quantity;
    }, 0);

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleOrder = async () => {
        try {
            const orderData = {
                ...formData,
                paymentMethod,
                paymentStatus: 'Pending',
                items: selectedItems.map(item => ({
                    productId: item.productId,
                    quantity: item.quantity
                })),
                total: totalPrice
            };

            const res = await axios.post('http://localhost:3008/api/paymentMethod/create', orderData);
            alert('Đặt hàng thành công!');
            console.log(res.data);
        } catch (err) {
            console.error('Đặt hàng thất bại:', err);
            alert('Đặt hàng thất bại!');
        }
    };

    return (
        <div className="payment-container">
            <div className="address-section">
                <h3>Địa chỉ nhận hàng</h3>
                <form className="address-form">
                    <input type="text" name="fullName" placeholder="Họ và tên" required onChange={handleInputChange} />
                    <input type="email" name="email" placeholder="Email" required onChange={handleInputChange} />
                    <input type="text" name="phone" placeholder="Phone" required onChange={handleInputChange} />
                    <input type="text" name="city" placeholder="Tỉnh / Thành phố" required onChange={handleInputChange} />
                    <input type="text" name="address" placeholder="Địa chỉ chi tiết" required onChange={handleInputChange} />
                </form>
            </div>

            <div className="payment-method-section">
                <h3>Phương thức thanh toán</h3>
                <div className="payment-options">
                    {['cod', 'bank', 'credit'].map((method) => (
                        <label key={method}>
                            <input
                                type="radio"
                                name="paymentMethod"
                                value={method}
                                checked={paymentMethod === method}
                                onChange={(e) => setPaymentMethod(e.target.value)}
                            />
                            {method === 'cod' && 'Thanh toán khi nhận hàng (COD)'}
                            {method === 'bank' && 'Chuyển khoản ngân hàng'}
                            {method === 'credit' && 'Thẻ tín dụng / Ghi nợ'}
                        </label>
                    ))}
                </div>
            </div>

            <div className="product-summary">
                <h3>Sản phẩm</h3>
                {selectedItems.map((item, index) => {
                    const prod = getProductById(item.productId);
                    if (!prod) return null;
                    const price = prod.discounted_price || prod.price || 0;

                    return (
                        <div className="summary-item" key={index}>
                            <img src={prod.img?.[0] || prod.image?.[0] || 'fallback.jpg'} alt={prod.name} />
                            <div>
                                <p>{prod.name}</p>
                                <p>Số lượng: {item.quantity}</p>
                                <p>Giá: đ{(price * item.quantity).toLocaleString('vi-VN')}</p>
                            </div>
                        </div>
                    );
                })}
            </div>

            <div className="payment-summary">
                <h3>Tổng thanh toán</h3>
                <p>đ{totalPrice.toLocaleString('vi-VN')}</p>
                <button className="btn-confirm" onClick={handleOrder}>Đặt hàng</button>
            </div>
        </div>
    );
};
