import { createContext, useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export const AppContext = createContext();

export const AppContextProvider = ({ children }) => {

  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [user, setUser] = useState(null);
  const [seller, setSeller] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));


  const fetchCategories = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/category/all');
      setCategories(res.data || []);
    } catch (error) {
      console.error('Lỗi khi lấy danh mục:', error.message);
    }
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:4003/api/product/all');
      setProducts(res.data.products || []);
    } catch (error) {
      console.error('Lỗi khi lấy sản phẩm:', error.message);
    }
  };
  const fetchSellerByUserId = async (userId) => {
    try {
      const res = await axios.get(`http://localhost:3006/api/seller/by-user/${userId}`);
      const sellerData = res.data;
      setSeller(sellerData);
      localStorage.setItem('sellerId', sellerData._id); // ✅ Lưu sellerId vào localStorage
    } catch (error) {
      console.error("Không thể lấy seller từ userId:", error.message);
    }
  };
  useEffect(() => {
    fetchProducts();
    fetchCategories();
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }

  }, []);


  const handleLogin = (userData, token) => {
    setUser(userData);
    setToken(token);
    localStorage.setItem('user', JSON.stringify(userData));
    localStorage.setItem('token', token);
    if (userData.role === 'seller') {
      fetchSellerByUserId(userData._id || userData.id); // ✅ dùng đúng ID
    }
  };
  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('user');
    localStorage.removeItem('token');

  };
  const handleSellerLogin = (userData, token) => {
    setSeller(userData);
    setUser(userData);
    setToken(token);
    localStorage.setItem('user', JSON.stringify(userData));
    localStorage.setItem('token', token);
  };


  const handleSellerLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('user');
    localStorage.removeItem('token');
    clearSellerFormData();
  };


  const clearSellerFormData = () => {
    localStorage.removeItem('storeName');
    localStorage.removeItem('storeAddress');
    localStorage.removeItem('phone');
    localStorage.removeItem('images');
  };

  const value = {
    categories,
    setCategories,
    products,
    setProducts,
    user,
    setUser,
    token,
    handleLogin,
    handleLogout,
    seller,
    setSeller,
    handleSellerLogin,
    handleSellerLogout
  };


  return (
    <AppContext.Provider value={value}>
      {children}
    </AppContext.Provider>
  );
};

export const useAppContext = () => useContext(AppContext);
