import React, { useState, useEffect } from 'react';
import './customerprofile.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const CustomerProfile = () => {
    const [formData, setFormData] = useState({
        fullName: '',
        phone: '',
        address: '',
        birthday: '',
        gender: '',
    });
    const { user, isAppInitialized } = useAppContext();
    const [isUserLoaded, setIsUserLoaded] = useState(false);

    useEffect(() => {
        if (user && user.id) {
            setIsUserLoaded(true);
        } else {
            setIsUserLoaded(false);
        }
    }, [user]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };



    if (!isAppInitialized || !isUserLoaded) {
        return <div>Đang tải thông tin người dùng...</div>;
    }

    if (!user || !user.id) {
        return <div>Không tìm thấy người dùng. Vui lòng đăng nhập.</div>;
    }
    useEffect(() => {
    const fetchCustomer = async () => {
        try {
            const token = localStorage.getItem('token');
            const config = {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            };
            const response = await axios.get(`http://localhost:3002/api/customer/user/${user.id}`, config);
            if (response.data) {
                setFormData({
                    fullName: response.data.fullName || '',
                    phone: response.data.phone || '',
                    address: response.data.address || '',
                    birthday: response.data.birthday ? response.data.birthday.slice(0, 10) : '',
                    gender: response.data.gender || '',
                });
            } else {
                setFormData({
                    fullName: '',
                    phone: '',
                    address: '',
                    birthday: '',
                    gender: '',
                });
            }
        } catch (error) {
            console.error('Lỗi khi tải thông tin customer:', error.response ? error.response.data : error.message);
            setFormData({
                fullName: '',
                phone: '',
                address: '',
                birthday: '',
                gender: '',
            });
        }
    };

    if (user && user.id) {
        fetchCustomer();
    }
}, [user]);

    return (
        <div className="customer-profile">
            <h2>Thông Tin Khách Hàng</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Họ và tên</label>
                    <input
                        type="text"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>
                <div className="form-group">
                    <label>Điện Thoại</label>
                    <input
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>
                <div className="form-group">
                    <label>Địa chỉ</label>
                    <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>

                <div className="form-group">
                    <label>Sinh Nhật</label>
                    <input
                        type="date"
                        name="birthday"
                        value={formData.birthday}
                        onChange={handleChange}
                        required
                        className="input-date"
                    />
                </div>

                <div className="form-group">
                    <label>Giới tính</label>
                    <select
                        name="gender"
                        value={formData.gender}
                        onChange={handleChange}
                        required
                        className="input-gender"
                    >
                        <option value="">Chọn giới tính</option>
                        <option value="male">Nam</option>
                        <option value="female">Nữ</option>
                        <option value="other">Khác</option>
                    </select>
                </div>
                <button type="submit" className='updateProfile'>
                    Lưu
                </button>
                <button type="submit" className='updateProfile'>
                    Cập nhật
                </button>
            </form>
        </div>
    );
};
