import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import './order.css';

export const Order = () => {
  const { user } = useAppContext();
  const [cartItems, setCartItems] = useState([]);
  const [transports, setTransports] = useState([]);
  const [selectedTransportId, setSelectedTransportId] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
        setCartItems(res.data);
      } catch (error) {
        console.error('Lỗi khi lấy giỏ hàng:', error);
      } finally {
        setLoading(false);
      }
    };

    const fetchTransports = async () => {
      try {
        const res = await axios.get('http://localhost:3004/api/transport/available');
        setTransports(res.data);
        if (res.data.length > 0) {
          setSelectedTransportId(res.data[0]._id); // chọn mặc định phương thức đầu tiên
        }
      } catch (error) {
        console.error('Lỗi khi lấy phương thức vận chuyển:', error);
      }
    };

    if (user?.id) {
      fetchCart();
      fetchTransports();
    }
  }, [user]);

  const totalProductPrice = cartItems.reduce(
    (total, item) => total + item.quantity * item.productId.price,
    0
  );

  const selectedTransport = transports.find((t) => t._id === selectedTransportId);
  const transportFee = selectedTransport ? selectedTransport.fee : 0;
  const totalPayment = totalProductPrice + transportFee;

  return (
    <div className="payment-container">
      {/* Address form */}
      <div className="address-section">
        <h3>Địa chỉ nhận hàng</h3>
        <form className="address-form">
          <input type="text" placeholder="Họ và tên" name="name" required />
          <input type="number" placeholder="Phone" name="phone" required />
          <input type="text" placeholder="Tỉnh / Thành phố" name="province" required />
          <input type="text" placeholder="Địa chỉ chi tiết" name="address" required />
        </form>
      </div>

      {/* Payment method */}
      <div className="payment-method-section">
        <h3>Phương thức thanh toán</h3>
        <label><input type="radio" name="paymentMethod" value="cod" defaultChecked /> Thanh toán COD</label>
      </div>

      {/* Transport selection */}
      <div className="transport-method-section">
        <h3>Phương thức vận chuyển</h3>
        <select value={selectedTransportId} onChange={(e) => setSelectedTransportId(e.target.value)}>
          {transports.map((t) => (
            <option key={t._id} value={t._id}>
              {t.shippingCarrier} - {t.fee.toLocaleString()} đ
            </option>
          ))}
        </select>
      </div>

      {/* Product summary */}
      <div className="product-summary">
        <h3>Sản phẩm</h3>
        {loading ? (
          <p>Đang tải...</p>
        ) : cartItems.length === 0 ? (
          <p>Giỏ hàng trống</p>
        ) : (
          cartItems.map((item) => (
            <div className="summary-item" key={item._id}>
              <img src={item.productId.image[0]} alt={item.productId.name} />
              <div>
                <p>{item.productId.name}</p>
                <p>Số lượng: {item.quantity}</p>
                <p>Giá: {item.productId.price.toLocaleString()} đ</p>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Total */}
      <div className="payment-summary">
        <h3>Tổng thanh toán</h3>
        <p>Sản phẩm: {totalProductPrice.toLocaleString()} đ</p>
        <p>Phí vận chuyển: {transportFee.toLocaleString()} đ</p>
        <p><strong>Tổng cộng: {totalPayment.toLocaleString()} đ</strong></p>
        <button className="btn-confirm">Đặt hàng</button>
      </div>
    </div>
  );
};
