import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { useAppContext } from '../../context/AppContext';
import { useParams, Link } from 'react-router-dom';
import { Button, Col, Row, Rate } from 'antd';

export const ProductDetails = () => {
  const { id } = useParams();
  const { product } = useAppContext(); // Lấy product từ AppContext
  const [thumbnail, setThumbnail] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);

  // Kiểm tra nếu products có giá trị
  const products = product ? product.find((item) => item.id === Number(id)) : null;

  // Kiểm tra nếu sản phẩm không tồn tại, hiển thị thông báo lỗi
  if (!products) {
    return <p>Không tìm thấy sản phẩm</p>;
  }

  // Cập nhật thumbnail khi sản phẩm thay đổi
  useEffect(() => {
    if (products && products.img && products.img.length > 0) {
      setThumbnail(products.img[0]);
    }
  }, [products]);

  // Lọc sản phẩm liên quan khi sản phẩm thay đổi
  useEffect(() => {
    if (products) {
      const related = product.filter((item) => item.category === products.category);
      setRelatedProducts(related.slice(0, 5)); // Lấy 5 sản phẩm liên quan
    }
  }, [products, product]);

  return (
    <>
      <ul>
        <li><Link to="/">Home</Link></li>
        <li><Link to="/products">Products</Link></li>
        <li><Link>{products.name}</Link></li>
      </ul>

      <div className="product-detail">
        <Row>
          <Col span={12}>
            {/* Ảnh sản phẩm */}
            <div className="product-image-gallery">
              <img src={thumbnail} alt={products.name} />
              <div className="thumbnail-list">
                {products.img.map((img, index) => (
                  <img key={index} src={img} alt={`Product Thumbnail ${index}`} onClick={() => setThumbnail(img)} />
                ))}
              </div>
            </div>
          </Col>

          <Col span={12}>
            <h2>{products.name}</h2>
            <p className="product-price">{products.price} VND</p>
            <Rate disabled defaultValue={products.rating || 0} />
            <p>{products.reviewsCount} đánh giá</p>
            <Button>Thêm vào giỏ hàng</Button>
            <Button type="primary">Mua ngay</Button>

            {/* Mô tả sản phẩm */}
            <h3>Mô tả sản phẩm</h3>
            <p>{products.description}</p>
          </Col>
        </Row>

        <div>
          <h3>Sản phẩm liên quan</h3>
          <Row>
            {relatedProducts.map((item) => (
              <Col span={6} key={item.id}>
                <div className="related-product">
                  <img src={item.img[0]} alt={item.name} />
                  <p>{item.name}</p>
                  <p>{item.price} VND</p>
                  <Button>Thêm vào giỏ hàng</Button>
                </div>
              </Col>
            ))}
          </Row>
        </div>

        <div>
          <h3>Thông tin người bán</h3>
          <p>Tên người bán: {products.seller.name}</p>
          <p>Sản phẩm đã bán: {products.seller.sold}</p>
        </div>
      </div>
    </>
  );
};
