import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './shoppages.css';
import { useParams } from 'react-router-dom';

export const ShopPages = () => {
    const { sellerId } = useParams();
    const [products, setProducts] = useState([]);
    const [shopInfo, setShopInfo] = useState({
        name: "Ten Shope",
        avatar: "/default-avatar.png", // Mặc định có ảnh đại diện
        lastOnline: "7 minutes ago",
    });

    // Lấy thông tin shop
    const fetchShopInfo = async () => {
        try {
            const res = await axios.get(`http://localhost:4003/api/seller/${sellerId}`);  // Giả sử bạn có API này để lấy thông tin shop
            console.log("Thông tin shop trả về:", res.data);
            setShopInfo({
                name: res.data.storeName,
                avatar: res.data.image || "/default-avatar.png",  // Nếu không có ảnh thì dùng ảnh mặc định
                lastOnline: "7 minutes ago",  // Thay đổi theo thực tế nếu có thông tin về thời gian online
            });
        } catch (error) {
            console.error('Lỗi khi lấy thông tin shop:', error);
        }
    };

    // Lấy sản phẩm của shop
    const fetchProducts = async () => {
        try {
            const res = await axios.get(`http://localhost:4003/api/product/sellers/${sellerId}`);
            console.log("Dữ liệu sản phẩm trả về:", res.data);
            setProducts(res.data);
        } catch (error) {
            console.error('Lỗi khi lấy sản phẩm:', error);
        }
    };

    // Chạy hàm fetch khi sellerId thay đổi
    useEffect(() => {
        fetchShopInfo();
        fetchProducts();
    }, [sellerId]);

    if (products.length === 0) {
        return <div>Loading products...</div>;
    }

    return (
        <div className="shop-page">
            <div className='shopIno'>
                <div className="shop-info-left">
                    <img
                        className="shop-avatar"
                        src={shopInfo.avatar}
                        alt="Shop Avatar"
                    />
                    <div className="shop-details">
                        <h2 className="shop-name">{shopInfo.name}</h2>
                        <p className="shop-online">Online: {shopInfo.lastOnline}</p>
                    </div>
                </div>
            </div>

            <div className="shop__category">
                <ul>
                    <li>Danh mục 1</li>
                    <li>Danh mục 2</li>
                    <li>Danh mục 3</li>
                </ul>
            </div>

            {/* <div className="shop-products">
                {products.map((product) => (
                    <div key={product._id} className="product-item">
                        <img
                            src={product.images || "/default-image.png"}  // Sử dụng ảnh mặc định nếu sản phẩm không có ảnh
                            alt={product.name}
                            className="product-image"
                        />
                        <div className="product-info">
                            <h3 className="product-name">{product.name}</h3>
                            <p className="product-category">{product.categoryId.name}</p>
                            <p className="product-price">${product.price}</p>
                        </div>
                    </div>
                ))}
            </div> */}
        </div>
    );
};
