import React, { useState } from 'react';
import './productdetails.css';
import { Button, Rate, Row, Col } from 'antd';

// Giả sử các ảnh đã được import từ file:
import product1 from '../../assets/images/product1.jpg';
import producdetails1 from '../../assets/images/producdetails1.jpg';
import producdetails2 from '../../assets/images/producdetails2.jpg';
import producdetails3 from '../../assets/images/producdetails3.jpg';
import producdetails4 from '../../assets/images/producdetails4.jpg';

export const ProductDetails = () => {
  const product = {
    id: 1,
    name: "Áo Thun Cotton CATTEEN Nam Nữ chuẩn form - Hình Thêu Cao Cấp trẻ trung",
    price: "đ117.000",
    img: [
      product1,
      producdetails1,
      producdetails2,
      producdetails3,
      producdetails4
    ],
    category: "Thời trang nam",
    rating: 4,
    discount_percent: "20%",
    stock_quantity: 55
  };

  const [thumbnail, setThumbnail] = useState(product.img[0]);

  return (
    <div className="product-details">
      <p className="breadcrumb">
        <span>Trang chủ</span> / <span>Sản phẩm</span> / <span>{product.category}</span> / <span className="highlight">{product.name}</span>
      </p>

      <Row gutter={32} className="product-content">
        <Col xs={24} md={10}>
          <div className="product-images">
            <div className="product-thumbnails">
              {product.img.map((img, idx) => (
                <img
                  key={idx}
                  src={img}
                  alt={`thumbnail-${idx}`}
                  className={`thumbnail ${thumbnail === img ? 'active' : ''}`}
                  onClick={() => setThumbnail(img)}
                />
              ))}
            </div>
            <div className="main-image">
              <img src={thumbnail} alt="selected" />
            </div>
          </div>
        </Col>

        <Col xs={24} md={14}>
          <div className="product-info">
            <h1>{product.name}</h1>

            <div className="rating-section">
              <Rate disabled defaultValue={product.rating} />
              <span className="rating-number">({product.rating})</span>
            </div>

            <div className="price-section">
              <p className="price-original">Giá bán: <span className="price">{product.price}</span></p>
              <p className="discount">Giảm giá: {product.discount_percent}</p>
              <p className="stock">Tồn kho: {product.stock_quantity} sản phẩm</p>
            </div>

            <p className="desc-title">Mô tả sản phẩm</p>
            <ul className="desc-list">
              <li>Chất liệu cotton mềm mịn, thoáng mát</li>
              <li>Kiểu dáng trẻ trung, phù hợp cả nam và nữ</li>
              <li>Form chuẩn, dễ phối đồ</li>
            </ul>

            <div className="action-buttons">
              <Button block>Thêm vào giỏ hàng</Button>
              <Button type="primary" block style={{ backgroundColor: '#615fff' }}>Mua ngay</Button>
            </div>
          </div>
        </Col>
      </Row>
    </div>
  );
};
