import React, { useState } from 'react';
import './payment.css';
import { useAppContext } from '../../context/AppContext';

export const Payment = () => {
    const { cartItems, product } = useAppContext();
    const [paymentMethod, setPaymentMethod] = useState('COD'); // Phương thức thanh toán mặc định là COD

    const selectedItems = cartItems.filter(item => item.selected);

    const getProductById = (id) => product.find((p) => p.id === id);

    const totalPrice = selectedItems.reduce((acc, item) => {
        const prod = getProductById(item.productId);
        if (!prod) return acc;
        return acc + prod.discounted_price * item.quantity;
    }, 0);

    const handlePaymentMethodChange = (event) => {
        setPaymentMethod(event.target.value);
    };

    return (
        <div className="payment-container">
            <h2>Thanh toán</h2>

            {/* Địa chỉ nhận hàng */}
            <div className="address-section">
                <h3>Địa chỉ nhận hàng</h3>
                <form className="address-form">
                    <input type="text" placeholder="Họ và tên" required />
                    <input type="text" placeholder="Số điện thoại" required />
                    <input type="text" placeholder="Tỉnh / Thành phố" required />
                    <input type="text" placeholder="Quận / Huyện / Phường / Xã" required />
                    <input type="text" placeholder="Địa chỉ chi tiết" required />
                </form>
            </div>

            {/* Tóm tắt sản phẩm */}
            <div className="product-summary">
                <h3>Sản phẩm</h3>
                {selectedItems.map((item, index) => {
                    const prod = getProductById(item.productId);
                    if (!prod) return null;
                    return (
                        <div className="summary-item" key={index}>
                            <img src={prod.img[0]} alt={prod.name} />
                            <div>
                                <p>{prod.name}</p>
                                <p>Số lượng: {item.quantity}</p>
                                <p>Giá: đ{(prod.discounted_price * item.quantity).toLocaleString('vi-VN')}</p>
                            </div>
                        </div>
                    );
                })}
            </div>

            {/* Phương thức thanh toán */}
            <div className="payment-method">
                <h3>Phương thức thanh toán</h3>
                <div>
                    <input 
                        type="radio" 
                        id="cod" 
                        name="paymentMethod" 
                        value="COD" 
                        checked={paymentMethod === 'COD'} 
                        onChange={handlePaymentMethodChange} 
                    />
                    <label htmlFor="cod">Thanh toán khi nhận hàng (COD)</label>
                </div>
                <div>
                    <input 
                        type="radio" 
                        id="creditCard" 
                        name="paymentMethod" 
                        value="CreditCard" 
                        checked={paymentMethod === 'CreditCard'} 
                        onChange={handlePaymentMethodChange} 
                    />
                    <label htmlFor="creditCard">Thẻ tín dụng</label>
                </div>
                <div>
                    <input 
                        type="radio" 
                        id="bankTransfer" 
                        name="paymentMethod" 
                        value="BankTransfer" 
                        checked={paymentMethod === 'BankTransfer'} 
                        onChange={handlePaymentMethodChange} 
                    />
                    <label htmlFor="bankTransfer">Chuyển khoản ngân hàng</label>
                </div>
                <div>
                    <input 
                        type="radio" 
                        id="eWallet" 
                        name="paymentMethod" 
                        value="eWallet" 
                        checked={paymentMethod === 'eWallet'} 
                        onChange={handlePaymentMethodChange} 
                    />
                    <label htmlFor="eWallet">Ví điện tử</label>
                </div>
            </div>

            {/* Tóm tắt thanh toán */}
            <div className="payment-summary">
                <h3>Tổng thanh toán</h3>
                <p>đ{totalPrice.toLocaleString('vi-VN')}</p>
                <button className="btn-confirm">Đặt hàng</button>
            </div>
        </div>
    );
};
